const EventEmitter = require('events').EventEmitter;
const React = require('react');
const TrackControls = require('./components/TrackControls.jsx');
const AudioPlayer = require('./lib/AudioPlayer');

class App extends React.Component {

	constructor(props) {
        super(props);

        this.state = {
            action: 'stop',
            duration: undefined,
            error: undefined,
            filename: undefined,
            pitch: 0.8,
            simpleFilter: undefined,
            status: [],
            t: 0,
            tempo: 0.8,
        };

        this.emitter = new EventEmitter();
        this.emitter.on('state', state => this.setState(state));
        this.emitter.on('status', status => {
            if (status === 'Done!') {
                this.setState({status: []});
            } else {
                this.setState({status: this.state.status.concat(status)});
            }
        });
        this.emitter.on('stop', () => this.stop());

        this.audioPlayer = new AudioPlayer({
            emitter: this.emitter,
            pitch: this.state.pitch,
            tempo: this.state.tempo,
        });
    }

    play() {
        if (this.state.action !== 'play') {
            this.audioPlayer.play();
            this.setState({action: 'play'});
        }
    }

    pause() {
        if (this.state.action === 'play') {
            this.audioPlayer.pause();
            this.setState({action: 'pause'});
        }
    }

    stop() {
        this.pause();
        this.audioPlayer.seekPercent(0);
        this.setState({action: 'stop', t: 0});
    }

	render (){
	  	return (

	  		<div className="App">
		      <header className="App-header">
		        <p>
		          Edit <code>src/App.js</code> and save to reload.
		        </p>
		        <a
		          className="App-link"
		          href="https://reactjs.org"
		          target="_blank"
		          rel="noopener noreferrer"
		        >
		          Learn React
		        </a>
		      </header>
		    </div>
	    )
	}

}

export default App;
