const EventEmitter = require('events').EventEmitter;
const React = require('react');
const TrackControls = require('./components/TrackControls.jsx');
const AudioPlayer = require('./lib/AudioPlayer');

class App extends React.Component {

  render (){
  	return (


  		<div className="container" style={{marginTop: '1em'}}>
                <div style={{marginBottom: '1em'}}>
                    <label
                        className="btn btn-primary btn-lg"
                        htmlFor="sas-file"
                        style={{marginRight: '0.25em'}}
                    >
                        <input
                            id="sas-file"
                            accept="audio"
                            type="file"
                            style={{display: 'none'}}
                            onChange={e => this.handleFileChange(e)}
                        />
                        Select MP3
                    </label>
                </div>

                <div className="row">
                    <div className="col-xs-5 col-sm-3 col-lg-2" style={{paddingTop: '6px'}}>
                        <TrackControls
                            action={this.state.action}
                            error={this.state.error}
                            filename={this.state.filename}
                            onPlay={() => this.play()}
                            onPause={() => this.pause()}
                            onStop={() => this.stop()}
                        />
                    </div>
                    <div className="col-xs-7 col-sm-9 col-lg-10">
                        <input
                            className="form-control"
                            type="range"
                            min="0"
                            max="100"
                            step="0.05"
                            value={this.percentDone()}
                            onChange={e => this.handleSeek(e)}
                        />
                    </div>
                </div>

                <div className="row">
                    <div className="col-xs-5 col-sm-3 col-lg-2" style={{paddingTop: '7px'}}>
                        Pitch ({this.state.pitch}x)
                    </div>
                    <div className="col-xs-7 col-sm-9 col-lg-10">
                        <input
                            className="form-control"
                            type="range"
                            min="0.05"
                            max="2"
                            step="0.05"
                            defaultValue={this.state.pitch}
                            onChange={e => this.handlePitchChange(e)}
                        />
                    </div>
                </div>

                <div className="row">
                    <div className="col-xs-5 col-sm-3 col-lg-2" style={{paddingTop: '7px'}}>
                        Tempo ({this.state.tempo}x)
                    </div>
                    <div className="col-xs-7 col-sm-9 col-lg-10">
                        <input
                            className="form-control"
                            type="range"
                            min="0.05"
                            max="2"
                            step="0.05"
                            defaultValue={this.state.tempo}
                            onChange={e => this.handleTempoChange(e)}
                        />
                    </div>
                </div>
            </div>



    )
  }

}

export default App;
